function BankAccount(aNumber,aName,aBalance){
    this.accountnumber =aNumber
    this.accountname=aName
    this.balance=aBalance

}
BankAccount.prototype.getbalance=function(){
    return `${this.accountname} has balance of INR -${this.balance}`
}
BankAccount.prototype.displayaccountinfo=function(){
    return `name of the account holder is ${this.accountname} -${this.accountnumber}-${this.balance}`
}
BankAccount.prototype.deposit=function(amount){
    if (amount<0){
        return 'deposit cant be negative'
    }else{
        this.balance+=amount
        return this.balance
    }
}
BankAccount.prototype.withdraw=function(amount){
    if(amount>this.balance){
        return 'insufficient balance'
    }else{
        this.balance-=amount
        return this.balance
    }
}
const c1= new BankAccount('SBI123','kushan',1000)
console.log(c1)
console.log(c1.getbalance())
console.log(c1.displayaccountinfo())
console.log(c1.deposit(100))
console.log(c1.withdraw(2000))